mapper.update:
UPDATE PROJECT
    SET STATUS_NO = #{statusNo}
WHERE NO = #{projectNo}

mapper.select:
SELECT P.NO NO , C.MAIN_CATEGORY MAIN_CATEGORY , C.SUB_CATEGORY SUB_CATEGORY , P.TITLE TITLE , PS.STATUS STATUS_NAME , M.NAME MEMBER_NAME FROM PROJECT P JOIN CATEGORY C ON P.CATEGORY_NO = C.NO JOIN PROJECT_STATUS PS ON PS.NO = P.STATUS_NO JOIN MEMBER M ON P.MEMBER_NO = M.NO
AND PS.STATUS = #{status}
AND C.MAIN_CATEGORY = #{mainCategory}
AND C.SUB_CATEGORY = #{subCategory}
AND M.NAME = #{creator}
AND P.TITLE LIKE '%' || #{projectTitle} || '%'
ORDER BY P.NO DESC

SELECT
    COUNT(*)
FROM PROJECT P
JOIN CATEGORY C ON P.CATEGORY_NO = C.NO
JOIN PROJECT_STATUS PS ON PS.NO = P.STATUS_NO
JOIN MEMBER M ON P.MEMBER_NO = M.NO


		AND C.MAIN_CATEGORY = #{mainCategory}


		AND C.SUB_CATEGORY = #{subCategory}


		AND PS.STATUS = #{status}


		AND M.NAME = #{creator}


		AND P.TITLE LIKE '%' || #{projectTitle} || '%'			


ORDER BY P.NO DESC
