mapper.insert:
INSERT INTO BACKER(
	NO
	, PROJECT_NO
	, MEMBER_NO
	, REWARD_NO
	, BACKING_DATE
) VALUES(
	SEQ_BACKER_NO.NEXTVAL
	, #{projectNo}
	, #{memberNo}
	, #{rewardNo}
	, SYSDATE)

INSERT INTO PAYMENT(
	NO
	, BACKER_NO
	, PROJECT_NO
	, PAYMENT_TYPE_NO
	, AMOUNT
) VALUES(
	SEQ_PAYMENT_NO.NEXTVAL
	, SEQ_BACKER_NO.CURRVAL
	, #{projectNo}
	, #{paymentTypeNo}
	, #{paymentAmount}
)

mapper.update:
  	UPDATE BACKER
    	SET RETRACT_YN='Y'
	WHERE NO = #{backNo}

UPDATE BACKER
	SET REWARD_NO = #{rewardNo}
WHERE NO = #{backNo}

  	UPDATE BACKER
	    SET PAYMENT_TYPE_NO = #{paymentTypeNo}
	WHERE NO = #{backNo}
