mapper.insert:
INSERT INTO BACKER(
	NO
	, PROJECT_NO
	, MEMBER_NO
	, REWARD_NO
	, BACKING_DATE
) VALUES(
	SEQ_BACKER_NO.NEXTVAL
	, #{projectNo}
	, #{memberNo}
	, #{rewardNo}
	, SYSDATE)

INSERT INTO PAYMENT(
	NO
	, BACKER_NO
	, PROJECT_NO
	, PAYMENT_TYPE_NO
	, AMOUNT
) VALUES(
	SEQ_PAYMENT_NO.NEXTVAL
	, SEQ_BACKER_NO.CURRVAL
	, #{projectNo}
	, #{paymentTypeNo}
	, #{paymentAmount}
)

INSERT INTO CARD(
    NO
    , PAYMENT_NO
    , CARD_NUMBER
    , VALID_THRU
    , CARD_PWD
    , BIRTH_DATE
) VALUES(
    SEQ_CARD_NO.NEXTVAL
    , SEQ_PAYMENT_NO.CURRVAL
    , #{cardNo}
    , #{validThru}
    , #{cardPwd}
    , #{birthDate}
)

INSERT INTO KAKAOPAY(
    NO
    , PAYMENT_NO
    , CUSTOMER_UID
) VALUES(
    SEQ_CARD_NO.NEXTVAL
    , SEQ_PAYMENT_NO.CURRVAL
    , #{customerUid}
)

mapper.update:
  	UPDATE BACKER
    	SET RETRACT_YN='Y'
	WHERE NO = #{backNo}

UPDATE BACKER
	SET REWARD_NO = #{rewardNo}
WHERE NO = #{backNo}

  	UPDATE BACKER
	    SET PAYMENT_TYPE_NO = #{paymentTypeNo}
	WHERE NO = #{backNo}
