mapper.select:
SELECT
	NO
FROM MEMBER
WHERE NO = #{no}
	AND PWD = #{pwd}

mapper.update:
  	UPDATE MEMBER
	SET PIC = #{pic}
		, UPDATE_DATE = SYSDATE
	WHERE NO = #{no}

UPDATE MEMBER
SET NAME = #{name}
		, UPDATE_DATE = SYSDATE
WHERE NO = #{no}

UPDATE MEMBER
SET INTRO = #{intro}
		, UPDATE_DATE = SYSDATE
WHERE NO = #{no}

UPDATE MEMBER
SET SITE_URL = #{siteUrl}
		, UPDATE_DATE = SYSDATE
WHERE NO = #{no}

UPDATE MEMBER
SET PWD = #{pwd}
		, UPDATE_DATE = SYSDATE
WHERE NO = #{no}
