mapper.update:
UPDATE PROJECT
    SET 
    CATEGORY_NO = #{categoryNo}
    , TITLE = #{title}
    , IMAGE_URL = #{imageUrl}
WHERE NO = #{no}

UPDATE PROJECT
    SET 
    GOAL_AMOUNT = #{goalAmount}
    , START_DATE = #{startDate}
    , END_DATE = #{endDate}
WHERE NO = #{no}

UPDATE REWARD
    SET 
        AMOUNT = #{amount}
        , NAME = #{name}
WHERE NO = #{no}

UPDATE PROJECT
    SET
        TXT_DESCRIPTION = #{txtDescription}
        , TXT_BUDGET = #{txtBudget}
        , TXT_SCHEDULE = #{txtSchedule}
        , TXT_TEAM = #{txtTeam}
        , TXT_ITEM = #{txtItem}
WHERE NO = #{no}
