mapper.select:
SELECT PROJECT_NO , MAIN_CATEGORY , SUB_CATEGORY , MEMBER_NAME , PROJECT_TITLE , REGEXP_REPLACE(REVERSE(REGEXP_REPLACE(REVERSE(TO_CHAR(CURRENT_AMOUNT)), '(\d{3})', '\1,')), '^,', '') AS CURRENT_AMOUNT , REGEXP_REPLACE(REVERSE(REGEXP_REPLACE(REVERSE(TO_CHAR(CURRENT_AMOUNT)), '(\d{3})', '\1,')), '^,', '') AS GOAL_AMOUNT , START_DATE , PROJECT_STATUS FROM ( SELECT P.NO AS PROJECT_NO , C.MAIN_CATEGORY AS MAIN_CATEGORY , C.SUB_CATEGORY AS SUB_CATEGORY , M.NAME AS MEMBER_NAME , P.TITLE AS PROJECT_TITLE , P.CURRENT_AMOUNT AS CURRENT_AMOUNT , P.GOAL_AMOUNT AS GOAL_AMOUNT , P.START_DATE AS START_DATE , PS.STATUS AS PROJECT_STATUS FROM PROJECT P JOIN CATEGORY C ON P.CATEGORY_NO = C.NO JOIN MEMBER M ON P.MEMBER_NO = M.NO JOIN PROJECT_STATUS PS ON P.STATUS_NO = PS.NO WHERE P.STATUS_NO NOT IN(1, 2, 4, 7) AND (P.TITLE LIKE '%' || '#{query}' || '%' OR P.TXT_DESCRIPTION LIKE '%' || '#{query}' || '%')
P.STATUS_NO = #{statusConditionNo}
CURRENT_AMOUNT*100/GOAL_AMOUNT >= #{achievementRateStart}
AND CURRENT_AMOUNT*100/GOAL_AMOUNT <= #{achievementRateEnd}
ORDER BY DECODE(PS.NO, 5, 1), DECODE(PS.NO, 3, 2), PS.NO DESC )
