-- MEMBER(회원) 테이블
DROP TABLE MEMBER CASCADE CONSTRAINTS;
CREATE TABLE MEMBER (
	NO	                NUMBER	            PRIMARY KEY
	, NAME	            VARCHAR2(42)	    NOT NULL
	, EMAIL	            VARCHAR2(150)	    NOT NULL        UNIQUE
	, PWD	            VARCHAR2(100)	    NOT NULL
	, PIC	            VARCHAR2(500)	    NULL
	, INTRO	            VARCHAR2(2500)	    NULL
	, SITE_URL	        VARCHAR2(500)	    NULL
	, ENROLL_DATE	    TIMESTAMP	        DEFAULT SYSDATE
	, UPDATE_DATE	    TIMESTAMP	        NULL
	, QUIT_YN	        CHAR(1)	            DEFAULT 'N' CHECK( QUIT_YN IN ('Y','N') )
);
COMMENT ON COLUMN MEMBER.NO IS '회원번호';
COMMENT ON COLUMN MEMBER.NAME IS '회원 이름 기본/변경가능';
COMMENT ON COLUMN MEMBER.EMAIL IS '회원 이메일(아이디)';
COMMENT ON COLUMN MEMBER.PWD IS '회원 비밀번호';
COMMENT ON COLUMN MEMBER.PIC IS '회원 프로필사진';
COMMENT ON COLUMN MEMBER.INTRO IS '회원 소개';
COMMENT ON COLUMN MEMBER.SITE_URL IS '회원 웹사이트';
COMMENT ON COLUMN MEMBER.ENROLL_DATE IS '회원 가입일자';
COMMENT ON COLUMN MEMBER.UPDATE_DATE IS '회원 정보 수정일자';
COMMENT ON COLUMN MEMBER.QUIT_YN IS '회원 탈퇴 여부';

-- CATEGORY(카테고리) 테이블
DROP TABLE CATEGORY CASCADE CONSTRAINTS;
CREATE TABLE CATEGORY (
	NO	                    NUMBER		        NOT NULL PRIMARY KEY
	, MAIN_CATEGORY_NO	    NUMBER		        NOT NULL
	, SUB_CATEGORY_NO	    NUMBER		        NOT NULL
	, MAIN_CATEGORY	        VARCHAR2(100)		NOT NULL
	, SUB_CATEGORY	        VARCHAR2(100)		NOT NULL
	, DEL_YN    	        CHAR(1)		        DEFAULT 'N'	CHECK( DEL_YN IN ('Y','N') )
);
COMMENT ON COLUMN CATEGORY.NO IS '카테고리 번호';
COMMENT ON COLUMN CATEGORY.MAIN_CATEGORY_NO IS '대분류 번호';
COMMENT ON COLUMN CATEGORY.SUB_CATEGORY_NO IS '소분류 번호';
COMMENT ON COLUMN CATEGORY.MAIN_CATEGORY IS '대분류 이름';
COMMENT ON COLUMN CATEGORY.SUB_CATEGORY IS '소분류 이름';
COMMENT ON COLUMN CATEGORY.DEL_YN IS '삭제여부';

-- AD_MEMBER(관리자) 테이블
DROP TABLE AD_MEMBER CASCADE CONSTRAINTS;
CREATE TABLE AD_MEMBER (
	NO	                NUMBER		        NOT NULL PRIMARY KEY
	, ID	            VARCHAR2(60)		NOT NULL
	, NAME	            VARCHAR2(42)		NOT NULL
	, PWD	            VARCHAR2(100)		NOT NULL
	, PIC	            VARCHAR2(500)		NULL
	, ENROLL_DATE	    TIMESTAMP	        DEFAULT SYSDATE	NOT NULL
	, QUIT_YN	        CHAR(1)	            DEFAULT 'N'	CHECK( QUIT_YN IN ('Y','N') )
);
COMMENT ON COLUMN AD_MEMBER.NO IS '관리자 번호';
COMMENT ON COLUMN AD_MEMBER.ID IS '관리자 계정 아이디';
COMMENT ON COLUMN AD_MEMBER.NAME IS '관리자 이름';
COMMENT ON COLUMN AD_MEMBER.PWD IS '관리자 비밀번호';
COMMENT ON COLUMN AD_MEMBER.PIC IS '관리자 프로필사진';
COMMENT ON COLUMN AD_MEMBER.ENROLL_DATE IS '계정생성일자';
COMMENT ON COLUMN AD_MEMBER.QUIT_YN IS '계정 활성화 여부';

-- REWARD(선물) 테이블
DROP TABLE REWARD CASCADE CONSTRAINTS;
CREATE TABLE REWARD(
    NO              NUMBER          PRIMARY KEY NOT NULL
