-- PROJECT(프로젝트) 테이블
DROP TABLE PROJECT CASCADE CONSTRAINTS;
CREATE TABLE PROJECT(
    NO                  NUMBER          PRIMARY KEY NOT NULL
    , STATUS_NO         NUMBER          DEFAULT 1
    , CATEGORY_NO       NUMBER          NULL
    , MEMBER_NO         NUMBER          NOT NULL
    , TITLE             VARCHAR2(150)   NULL
    , GOAL_AMOUNT       NUMBER          NULL
    , IMAGE_URL         VARCHAR2(150)   NULL
    , TXT_DESCRIPTION   CLOB            NULL
    , TXT_BUDGET        CLOB            NULL
    , TXT_SCHEDULE      CLOB            NULL
    , TXT_TEAM          CLOB            NULL
    , TXT_ITEM          CLOB            NULL
    , CURRENT_AMOUNT    NUMBER          DEFAULT 0
    , OK_YN             CHAR(1)         DEFAULT 'N' CHECK(OK_YN IN('Y' , 'N'))
    , OK_DATE           TIMESTAMP       NULL
    , ENROLL_DATE       TIMESTAMP       NULL
    , START_DATE        TIMESTAMP       NULL
    , END_DATE          TIMESTAMP       NULL
    , CAL_DATE          TIMESTAMP       NULL
    , DELETE_YN         CHAR(1)         DEFAULT 'N' CHECK(DELETE_YN IN('Y', 'N'))
);
COMMENT ON COLUMN PROJECT.NO IS '프로젝트 번호';
COMMENT ON COLUMN PROJECT.STATUS_NO IS '프로젝트 상태 번호';
COMMENT ON COLUMN PROJECT.CATEGORY_NO IS '카테고리 번호';
COMMENT ON COLUMN PROJECT.MEMBER_NO IS '창작자 번호';
COMMENT ON COLUMN PROJECT.TITLE IS '프로젝트 제목';
COMMENT ON COLUMN PROJECT.GOAL_AMOUNT IS '프로젝트 목표 금액';
COMMENT ON COLUMN PROJECT.IMAGE_URL IS '프로젝트 대표 이미지';
COMMENT ON COLUMN PROJECT.TXT_DESCRIPTION IS '프로젝트 소개';
COMMENT ON COLUMN PROJECT.TXT_BUDGET IS '프로젝트 예산 소개';
COMMENT ON COLUMN PROJECT.TXT_SCHEDULE IS '프로젝트 일정 소개';
COMMENT ON COLUMN PROJECT.TXT_TEAM IS '프로젝트 팀 소개';
COMMENT ON COLUMN PROJECT.TXT_ITEM IS '프로젝트 선물 소개';
COMMENT ON COLUMN PROJECT.CURRENT_AMOUNT IS '현재 모인 금액';
COMMENT ON COLUMN PROJECT.OK_YN IS '프로젝트 승인 여부';
COMMENT ON COLUMN PROJECT.OK_DATE IS '프로젝트 승인 날짜';
COMMENT ON COLUMN PROJECT.ENROLL_DATE IS '프로젝트 등록날짜';
COMMENT ON COLUMN PROJECT.START_DATE IS '프로젝트 시작일';
COMMENT ON COLUMN PROJECT.END_DATE IS '프로젝트 종료일';
COMMENT ON COLUMN PROJECT.CAL_DATE IS '프로젝트 정산일';
COMMENT ON COLUMN PROJECT.DELETE_YN IS '프로젝트 삭제 여부';

